import { useState } from "react";
import { AppBar } from "@mui/material";
import styles from "./_header.module.scss";
import { IconButton } from "@mui/material";
import "./mui-header-styles.scss";
import { ReactComponent as MenuIcon } from "assets/icons/bars-regular.svg";
import { ReactComponent as SettingsIcon } from "assets/icons/gear-thin.svg";
import { ReactComponent as SettingsIconHover } from "assets/icons/gear-regular.svg";
import { ReactComponent as SettingsIconSelected } from "assets/icons/gear-solid.svg";
import JapanGoHomeImage from "assets/images/header/japan-flag.png";

function ShowHideButton() {
  const [show, setShow] = useState(false);

  return (
    <IconButton
      className={`${show ? styles["show-hide-button-show"] : ""} ${
        styles["show-hide-button"]
      }`}
      size="large"
      disableRipple
      edge="start"
      onClick={() => {
        setShow(!show);
      }}
    >
      <MenuIcon />
    </IconButton>
  );
}

function HomeButton() {
  return (
    <div className={`${styles["home-button"]}`}>
      <img
        className={styles["japan-flag-home-image"]}
        src={JapanGoHomeImage}
        alt="Miss Strawberry Home Button"
      />
    </div>
  );
}

function SettingsButton() {
  const [settingsHover, setSettingsHover] = useState(false);
  const [selected, setSelected] = useState(false);

  let settingsIcon = (
    <SettingsIcon className={styles["default-header-button"]} />
  );

  if (settingsHover) {
    settingsIcon = (
      <SettingsIconHover className={styles["default-header-button"]} />
    );
  }

  if (selected) {
    settingsIcon = (
      <SettingsIconSelected className={styles["default-header-button"]} />
    );
  }

  return (
    <div
      onMouseEnter={() => {
        setSettingsHover(true);
      }}
      onMouseLeave={() => {
        setSettingsHover(false);
      }}
      onClick={() => {
        setSelected(!selected);
      }}
    >
      {settingsIcon}
    </div>
  );
}

function Header() {
  return (
    <div>
      <div>
        <h2>ENQUIRIES</h2>
        <div>
          General <br />
          +44 (0) 020 3613 4733 <br />
          Info@txowork.com
        </div>
      </div>
      <div>ADDRESS</div>
    </div>

    // <AppBar position="sticky" className={styles["header"]}>
    //   <div className={styles["header-section"]}>
    //     {/* <ShowHideButton /> */}
    //     <HomeButton />
    //   </div>
    //   <div className={styles["header-section"]} />
    //   <div className={styles["header-section"]}>
    //     <SettingsButton />
    //   </div>
    // </AppBar>
  );
}

export { Header };
