import { pickRandomArrayValue } from "../../helpers/helpers";
import { futureSimple } from "./future-simple";

export const subjectVerb = (settings) => {
  const tenses = [futureSimple];

  const randomTense = pickRandomArrayValue(tenses);

  return randomTense(settings);
};
