import React, { useRef, useEffect } from "react";
import carouselStyles from "./styles/_carousel.module.scss";
import heroStyles from "./styles/_hero.module.scss";
import imageInfoStyles from "./styles/_image-info.module.scss";
import formStyles from "./styles/_form.module.scss";
import footerStyles from "./styles/_footer.module.scss";
import headerStyles from "./styles/_header.module.scss";
import emptyScrollStyles from "./styles/_empty-scroll.module.scss";
import homeStyles from "./_home.module.scss";
import MeetingRoom from "assets/images/carousel_image.jpg";
import { ReactComponent as TxoLogo } from "assets/icons/txo_logo_2.svg";
import { ReactComponent as ThirdwayLogo } from "assets/icons/thirdway.svg";
import ScrollTrigger from "gsap/ScrollTrigger";
import ScrollToPlugin from "gsap/ScrollToPlugin";
import gsap from "gsap";
gsap.registerPlugin(ScrollTrigger);
gsap.registerPlugin(ScrollToPlugin);

function Home() {
  return (
    <>
      <Header />
      <div className={homeStyles["home-container"]}>
        <EmptyScrollBlock />
        <HeroBlock />
        <CarouselBlock />
        <ImageInfoBlock />
        <FormBlock />
        <Footer />
      </div>
    </>
  );
}

function Header() {
  const headerRef = useRef(null);

  const txoLogoContainerRef = useRef(null);

  const txoLogoRef = useRef(null);

  useEffect(() => {
    // const txoLogoRefContainerElement = txoLogoContainerRef.current;

    // gsap.to(txoLogoRefContainerElement, {
    //   // scale: 0.5,
    //   transformOrigin: "top 20px",
    //   scrollTrigger: {
    //     start: "top top",
    //     end: "+=50%",
    //     scrub: true,
    //     markers: true,
    //   },
    // });

    const txoLogoRefElement = txoLogoRef.current;

    ScrollTrigger.refresh();

    gsap.to(txoLogoRefElement, {
      scale: 0.5,
      // pin: true,
      // y: 0,
      // bottom: 0,
      transformOrigin: "top 20px",
      scrollTrigger: {
        start: "top top",
        // end: "+=50%",
        scrub: true,
        trigger: "#hero-block",
        // trigger: "#header-items",
        // endTrigger: "section.three",
        // end: "bottom top",
        pin: "#pin-me",
        markers: true,
      },
    });

    // ScrollTrigger.create({
    //   trigger: "section.two",
    //   start: "top top",
    //   endTrigger: "section.three",
    //   end: "bottom top",
    //   pin: ".pin-me",
    // });

    // const timeline = gsap.timeline({ paused: true });

    // gsap.fromTo(
    //   txoLogoRefElement,
    //   { opacity: "0" },
    //   {
    //     opacity: "1",
    //     scrollTrigger: {
    //       scrub: 0.1,
    //       start: "top top",
    //       end: "bottom top",
    //       trigger: "#animation-trigger-txo",
    //     },
    //   }
    // );

    // gsap.to(txoLogoRefElement, {
    //   height: "100vw",
    //   // pin: true,
    //   scale: "0.3",
    //   x: "-170",
    //   // y: "1000",

    //   // width: "",
    //   scrollTrigger: {
    //     start: "top top",
    //     end: "bottom top",
    //     scrub: true,
    //     pin: true,
    //     trigger: "#animation-trigger-txo",
    //   },
    // });

    // gsap.from(txoLogoRefElement, {
    //   scale: 7,
    //   y: 600,

    //   scrollTrigger: {
    //     start: "top top",
    //     end: "bottom top",
    //     scrub: true,
    //     pin: true,
    //     trigger: "#animation-trigger-txo",
    //   },
    // });

    const element = headerRef.current;
    gsap.fromTo(
      element,
      { opacity: "0" },
      {
        opacity: "1",
        scrollTrigger: {
          scrub: 0.1,
          start: "top top",
          end: "bottom top",
          trigger: "#animation-trigger-txo",
        },
      }
    );
  }, []);

  return (
    <header className={headerStyles["header"]}>
      {/* <div
        id="txo-logo-wrapper"
        className={headerStyles["txo-logo-wrapper"]}
        ref={txoLogoContainerRef}
      > */}
      <TxoLogo
        id="pin-me"
        ref={txoLogoRef}
        className={headerStyles["txo-logo"]}
      />
      {/* </div> */}
      <div
        id="header-items"
        className={headerStyles["header-items"]}
        ref={headerRef}
      >
        <ul>
          <li>ENQUIRIES</li>
          <li>General</li>
          <li>+44 (0) 020 3613 4733</li>
          <li>Info@txowork.com</li>
        </ul>
        <ul>
          <li>&nbsp;</li>
          <li>Sales</li>
          <li>+44 (0) 020 3613 4733 </li>
          <li>Info@txowork.com</li>
        </ul>
        <ul>
          <li>ADDRESS</li>
          <li>Morelands</li>
          <li>5-23 Old Street</li>
          <li>London EC1V 9HL</li>
        </ul>
        <ul>
          <li>CONNECT</li>
          <li>Instagram</li>
          <li>LinkedIn</li>
          <li>Facebook</li>
        </ul>
      </div>
    </header>
  );
}

function EmptyScrollBlock() {
  const windowHeight = useRef(document.createElement("div"));

  return (
    <div
      id="animation-trigger-txo"
      className={emptyScrollStyles["empty-scroll-block"]}
      ref={windowHeight}
    />
  );
}

function HeroBlock() {
  const heroIntroText = useRef(null);

  useEffect(() => {
    const element = heroIntroText.current;
    gsap.fromTo(
      element,
      { opacity: "0" },
      {
