import { subjectVerb } from "./sentence-builders/subject-verb/subject-verb-master";
import { pickRandomArrayValue } from "./helpers/helpers";
import { subjectVerbObject } from "./sentence-builders/subject-verb-object/subject-verb-object-master";

export const buildRandomSentence = (settings) => {
  const sentenceBuilders = [subjectVerbObject, subjectVerb];

  const randomSentenceBuilder = pickRandomArrayValue(sentenceBuilders);

  const randomSentence = randomSentenceBuilder(settings);

  return randomSentence;
};

//Subject + verb + place
// A man ate at the park
