import styles from "./_square-button.module.scss";

type Props = {
  buttonStyle?: string;
  text: string;
  onClickButton: React.MouseEventHandler;
  disabled?: boolean;
};

function SquareButton({
  buttonStyle = "fill",
  text,
  onClickButton,
  disabled,
}: Props) {
  let squareButtonStyles = `${styles["default-square-button"]}`;

  if (disabled) {
    squareButtonStyles = `${squareButtonStyles} ${styles["disabled"]}`;
  }

  if (!disabled) {
    switch (buttonStyle) {
      case "fill":
        squareButtonStyles = `${squareButtonStyles} ${styles["fill"]}`;
        break;
      case "outline":
        squareButtonStyles = `${squareButtonStyles} ${styles["outline"]}`;
    }
  }

  return (
    <button
      onClick={(event) => {
        onClickButton(event);
      }}
      className={squareButtonStyles}
    >
      {text}
    </button>
  );
}

export { SquareButton };
